mapper.select:
SELECT TB.* FROM ( SELECT X.* FROM (
SELECT a.ATCH_FILE_ID, SUBSTRING_INDEX(FILE_STRE_COURS, '/',-3) AS FILE_STRE_COURS, a.STRE_FILE_NM, a.FILE_EXTSN, a.REPORT_SEQ, a.FILE_THUMNAIL , a.ORIGNL_FILE_NM , CASE WHEN a.FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN a.FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN a.FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType , a.PLAY_TIME, a.FILE_WIDTH , a.FILE_HEIGHT, a.UseYN FROM lettnfiledetail a WHERE 1=1
and A.STRE_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
and A.ORIGNL_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY a.ATCH_FILE_ID DESC )X WHERE 1=1
and X.mediaType = #{conType}
and X.mediaType != #{notConType}
) TB LIMIT #{firstIndex} , #{recordCountPerPage}

SELECT ifnull(count(*),0) FROM ( SELECT CASE WHEN FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType FROM lettnfiledetail WHERE 1=1
and STRE_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
and ORIGNL_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
) X WHERE 1=1
and X.mediaType = #{conType}
and X.mediaType != #{notConType}

mapper.update:
	    UPDATE lettnfiledetail SET PLAY_TIME = #{playTime,jdbcType=VARCHAR},
                                FILE_WIDTH = #{fileWidth,jdbcType=VARCHAR}, 
                                FILE_HEIGHT =  #{fileHeight,jdbcType=VARCHAR} 
	    WHERE ATCH_FILE_ID = #{atchFileId}
