mapper.select:
       SELECT   IFNULL((max(DETAIL_SORT ) +1),1)       
	    FROM TB_DISPLAYDETAIL a
	    WHERE DISPLAY_SEQ = #{displaySeq}

mapper.update:
UPDATE TB_DISPLAYDETAIL SET   DETAIL_SORT = #{detailSort,jdbcType=VARCHAR }
                       ,  DETAIL_TIME = #{detailTime,jdbcType=VARCHAR }
                       ,  LAST_UPDT_PNTTM = now()
                       ,  LAST_UPDUSR_ID = #{userId,jdbcType=VARCHAR}
WHERE DETAIL_SEQ = #{detailSeq}

UPDATE TB_DISPLAYDETAIL SET DETAIL_TIME = #{detailTime,jdbcType=VARCHAR }
WHERE DETAIL_SEQ = #{detailSeq};

 UPDATE tb_displaypageinfo SET 
      DISPALY_PAGECNT = (SELECT NVL(COUNT(*),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
      , DISPALY_TOTALTIME = (SELECT NVL(sum(DETAIL_TIME),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
WHERE  DISPLAY_SEQ = #{displaySeq} ;


		UPDATE TB_DISPLAYDETAIL SET DETAIL_SORT = DETAIL_SORT - 1
		WHERE detail_seq = ( 
			SELECT detail_seq FROM (
			SELECT detail_seq FROM tb_displaydetail 
			WHERE  DETAIL_SORT > (SELECT DETAIL_SORT FROM tb_displaydetail WHERE detail_seq =  #{detailSeq}) ORDER BY detail_sort ASC
			)   X 
			limit 1
		);

		UPDATE TB_DISPLAYDETAIL SET DETAIL_SORT = DETAIL_SORT +1 
        WHERE detail_seq = #{detailSeq};


      UPDATE TB_DISPLAYDETAIL SET DETAIL_SORT = DETAIL_SORT + 1
		WHERE DETAIL_SEQ = ( 
			SELECT DETAIL_SEQ FROM (
			SELECT DETAIL_SEQ FROM TB_DISPLAYDETAIL 
			WHERE  DETAIL_SORT < (SELECT DETAIL_SORT FROM TB_DISPLAYDETAIL WHERE DETAIL_SEQ =  #{detailSeq}) ORDER BY DETAIL_SORT desc 
			)   X 
			limit 1
		);
		UPDATE TB_DISPLAYDETAIL SET DETAIL_SORT = DETAIL_SORT - 1 
        WHERE DETAIL_SEQ = #{detailSeq};

UPDATE TB_DISPLAYPAGEINFO aa SET (DISPALY_PAGECNT, DISPALY_TOTALTIME, UPDATE_CHANGE, UPDATE_DATE) = (SELECT NVL(COUNT(*),0) DISPALY_PAGECNT, NVL(SUM(b.DETAIL_TIME),0) DISPALY_TOTALTIME, 'N' UPDATE_CHANGE, NULL UPDATE_DATE FROM TB_DISPLAYDETAIL b, TB_DISPLAYPAGEINFO a, TB_REPORTINFO c WHERE a.DISPLAY_SEQ = b.DISPLAY_SEQ AND a.DISPLAY_SEQ IN
#{item}
AND aa.DISPLAY_SEQ = a.DISPLAY_SEQ AND c.REPORT_SEQ = b.REPORT_SEQ GROUP BY a.DISPLAY_SEQ) WHERE aa.DISPLAY_SEQ IN (SELECT a.DISPLAY_SEQ FROM TB_DISPLAYDETAIL b, TB_DISPLAYPAGEINFO a, TB_REPORTINFO c WHERE a.DISPLAY_SEQ = b.DISPLAY_SEQ AND a.DISPLAY_SEQ IN
#{item}
AND c.REPORT_SEQ = b.REPORT_SEQ GROUP BY a.DISPLAY_SEQ)

mapper.delete:
DELETE FROM      TB_DISPLAYDETAIL
WHERE DISPLAY_SEQ = #{displaySeq}  ;

UPDATE TB_DISPLAYPAGEINFO SET 
      DISPALY_PAGECNT = (SELECT NVL(COUNT(*),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
      , DISPALY_TOTALTIME = (SELECT NVL(sum(DETAIL_TIME),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
WHERE  DISPLAY_SEQ = #{displaySeq}

DELETE FROM   TB_DISPLAYDETAIL   
WHERE DETAIL_SEQ = #{detailSeq}   ;

UPDATE TB_DISPLAYPAGEINFO SET 
      DISPALY_PAGECNT = (SELECT NVL(COUNT(*),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
      , DISPALY_TOTALTIME = (SELECT NVL(sum(DETAIL_TIME),0) FROM tb_displaydetail WHERE DISPLAY_SEQ = #{displaySeq})
WHERE  DISPLAY_SEQ = #{displaySeq} ;

DELETE FROM TB_DISPLAYDETAIL  
WHERE REPORT_SEQ IN 

