mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.REPORT_SEQ, a.REPORT_TITLE, a.REPORT_DC, case a.REPORT_GUBUN when 'reportGubun_3' then CONCAT(replace(a.REPORT_URL, #{replacePath}, '') , a.REPORT_DC) when 'reportGubun_4' then CONCAT(replace(a.REPORT_URL, #{replacePath}, '') , a.REPORT_PREVIEW) else a.REPORT_URL end REPORT_URL, case a.REPORT_GUBUN when 'reportGubun_3' then CONCAT(replace(a.REPORT_URL, #{replacePath}, '') , a.REPORT_DC) when 'reportGubun_4' then CONCAT(replace(a.REPORT_URL, #{replacePath}, '') , a.REPORT_DC) else a.REPORT_URL end REPORT_URL_REAL, a.REPORT_GUBUN, a.REPORT_UESYN reportUseYn, a.FRST_REGIST_PNTTM, a.FRST_REGISTER_ID, case a.REPORT_GUBUN when 'reportGubun_3' then (select CONCAT(ATCH_FILE_ID, ',',ifnull(FILE_WIDTH, ''), ',', ifnull(FILE_HEIGHT, ''), ',', ifnull(PLAY_TIME, '')) from lettnfiledetail b where b.STRE_FILE_NM = a.REPORT_DC) when 'reportGubun_4' then (select CONCAT(ATCH_FILE_ID, ',',ifnull(FILE_WIDTH, ''), ',', ifnull(FILE_HEIGHT, ''), ',', ifnull(PLAY_TIME, '')) from lettnfiledetail b where b.STRE_FILE_NM = a.REPORT_DC) ELSE REPORT_PREVIEW end REPORT_PREVIEW FROM tb_reportinfo a WHERE 1=1
and a.REPORT_DC LIKE CONCAT('%',#{searchKeyword},'%')
and a.REPORT_TITLE LIKE CONCAT('%',#{searchKeyword},'%')
and REPORT_UESYN = #{reportUseYn}
and REPORT_GUBUN IN
#{item}
ORDER BY a.REPORT_SEQ DESC ) TB ORDER BY TB.REPORT_SEQ DESC limit #{firstIndex} , #{recordCountPerPage}

mapper.insert:
 insert into tb_reportinfo( REPORT_TITLE, REPORT_DC, REPORT_URL, REPORT_GUBUN, 
                               REPORT_UESYN, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID)
 values (#{reportTitle, jdbcType=VARCHAR}, #{reportDc,jdbcType=VARCHAR}, #{reportUrl}, #{reportGubun,jdbcType=VARCHAR }
         , #{reportUseYn,jdbcType=VARCHAR },  now(), #{userId,jdbcType=VARCHAR},  now() , #{userId,jdbcType=VARCHAR});

insert into lettnfiledetail(ATCH_FILE_ID, FILE_STRE_COURS, STRE_FILE_NM, ORIGNL_FILE_NM, FILE_EXTSN, FILE_SIZE, 
   FILE_THUMNAIL, PLAY_TIME, FRST_REGISTER_ID, FRST_REGIST_PNTTM, REPORT_SEQ)
   values ( #{reportTitle, jdbcType=VARCHAR} , #{reportUrl}, #{reportDc,jdbcType=VARCHAR}, #{reportTitle, jdbcType=VARCHAR}, 'url'  , 0, 
    ''   , '1', #{userId,jdbcType=VARCHAR},  now(),
    (SELECT MAX(REPORT_SEQ) FROM TB_REPORTINFO)
    )

mapper.update:
update tb_reportinfo set REPORT_TITLE = #{reportTitle}
                       ,  REPORT_DC = #{reportDc,jdbcType=VARCHAR }
                       ,  REPORT_URL = #{reportUrl,jdbcType=VARCHAR }
                       ,  REPORT_GUBUN = #{reportGubun}
                       ,  REPORT_UESYN =  #{reportUseYn,jdbcType=VARCHAR }
                       ,  LAST_UPDT_PNTTM =  now()
                       ,  LAST_UPDUSR_ID = #{userId,jdbcType=VARCHAR}
where REPORT_SEQ = #{reportSeq}
