mapper.select:
SELECT TB.* FROM (
SELECT X.* FROM ( SELECT a.ATCH_FILE_ID, SUBSTRING_INDEX(FILE_STRE_COURS, '/',-3) AS FILE_STRE_COURS, a.STRE_FILE_NM, a.FILE_EXTSN, a.REPORT_SEQ, a.FILE_THUMNAIL , a.ORIGNL_FILE_NM , CASE WHEN a.FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN a.FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN a.FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType , a.PLAY_TIME, a.FILE_WIDTH , a.FILE_HEIGHT, a.UseYN FROM LETTNFILEDETAIL a WHERE 1=1
AND A.STRE_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.ORIGNL_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY a.ATCH_FILE_ID DESC )X WHERE 1=1
AND X.mediaType = #{conType}
AND X.mediaType != #{notConType}
) TB limit #{firstIndex} , #{recordCountPerPage}

SELECT IFNULL(COUNT(*),0) FROM ( SELECT CASE WHEN FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType FROM LETTNFILEDETAIL WHERE 1=1
and STRE_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
and ORIGNL_FILE_NM LIKE CONCAT('%',#{searchKeyword},'%')
) X WHERE 1=1
AND X.mediaType = #{conType}
AND X.mediaType != #{notConType}

mapper.insert:

INSERT INTO TB_REPORTINFO( REPORT_TITLE, REPORT_DC, REPORT_URL, REPORT_GUBUN, 
                            REPORT_UESYN, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID, REPORT_PREVIEW)
VALUES (#{orignlFileNm}, #{streFileNm}, #{fileStreCours}, #{reportGubun}
      , 'Y',  now(), #{userId,jdbcType=VARCHAR},  now() , #{userId,jdbcType=VARCHAR}, #{fileThumnail ,jdbcType=VARCHAR});

INSERT INTO LETTNFILEDETAIL(ATCH_FILE_ID, FILE_STRE_COURS, STRE_FILE_NM, ORIGNL_FILE_NM, FILE_EXTSN, FILE_SIZE, 
FILE_THUMNAIL, PLAY_TIME, FRST_REGISTER_ID, FRST_REGIST_PNTTM, REPORT_SEQ,FILE_WIDTH, FILE_HEIGHT )
VALUES ( #{atchFileId} , #{fileStreCours}, #{streFileNm}, #{orignlFileNm}, #{fileExtsn}  , #{fileSize}, 
#{fileThumnail ,jdbcType=VARCHAR}   , #{playTime,  jdbcType=VARCHAR }, #{userId,jdbcType=VARCHAR},  now(),
(SELECT MAX(REPORT_SEQ) FROM TB_REPORTINFO),  #{fileWidth,  jdbcType=VARCHAR },  #{fileHeight,  jdbcType=VARCHAR }
 )

mapper.update:
	    UPDATE LETTNFILEDETAIL SET PLAY_TIME = #{playTime,jdbcType=VARCHAR},
                                FILE_WIDTH = #{fileWidth,jdbcType=VARCHAR}, 
                                FILE_HEIGHT =  #{fileHeight,jdbcType=VARCHAR} 
	    WHERE ATCH_FILE_ID = #{atchFileId}

