mapper.select:
SELECT X.*, X1.CENTER_NM FROM ( SELECT b.AGENT_CODE, b.AGENT_NM , a.SCH_CODE, b.AGENT_REMARK, b.CENTER_ID, b.AGENT_FLOOR FROM TB_AGENTINFO b LEFT OUTER JOIN (SELECT a.SCH_CODE, a.AGENT_CODE FROM TB_SENDMESSAGEINFO a WHERE SCH_CODE = #{schCode}) a on a.AGENT_CODE = b.AGENT_CODE WHERE b.AGENT_USEYN ='Y'
AND b.PART_ID= #{partId}
ORDER BY b.AGENT_REMARK asc ) X, TB_CENTERINFO X1 WHERE X.CENTER_ID = X1.CENTER_ID

mapper.insert:
INSERT INTO TB_SENDMESSAGEINFO (SCH_CODE,AGENT_CODE, MSG_SEND, MSG_SENDREGDATE, MSG_RECCHECK, MSG_RECUPDATE) SELECT #{schCode}, b.AGENT_CODE, 'Y', now(), 'N', NULL FROM TB_AGENTINFO b LEFT OUTER JOIN (SELECT a.SCH_CODE, a.AGENT_CODE FROM TB_SENDMESSAGEINFO a WHERE SCH_CODE = #{schCode}) a ON a.AGENT_CODE = b.AGENT_CODE WHERE a.SCH_CODE IS NULL
AND b.AGENT_CODE IN
#{item}

mapper.update:
UPDATE TB_SENDMESSAGEINFO  set MSG_RECCHECK = 'N' ,  
							   MSG_RECUPDATE =  null
WHERE SCH_CODE = #{schCode}

update TB_SENDMESSAGEINFO SET MSG_RECCHECK = #{msgRecCheck}, 
							  MSG_RECUPDATE = now()
WHERE SCH_CODE = #{schCode} and AGENT_CODE = #{agentCode}

mapper.delete:
DELETE FROM TB_SENDMESSAGEINFO WHERE SCH_CODE = #{schCode} AND AGENT_CODE IN (SELECT AGENT_CODE FROM TB_AGENTINFO WHERE AGENT_CODE NOT IN
#{item}
)

DELETE FROM TB_SENDMESSAGEINFO 
WHERE 1=1  

    AND SCH_CODE = #{schCode}


    AND SEND_MSGSEQ = #{sendMsgSeq}
