mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( select a.MSG_SEQ as "msgSeq", a.XML_PROCESS_NAME as "xmlProcessName", c.PROCESS_REMARK as "processRemark", a.SEND_RESULT as "sendResult", a.SEND_REGDATE as "sendRegDate", b.AGENT_NM as "agentNm", a.AGENT_PLAYTIME as "didPlayTime" , a.AGENT_MAC as "agentMac", b.AGENT_IP as "agentIp", a.ERROR_MESSAGE as "errorMessage" FROM TB_AGENTSENDMESSAGEINFO a, TB_AGENTINFO b, TB_SENDMESSAGETYPR c WHERE a.AGENT_CODE = b.AGENT_CODE and a.XML_PROCESS_NAME = c.XML_PROCESS_NAME
and c.XML_PROCESS_NAME LIKE CONCAT('%',#{searchKeyword},'%')
and a.AGENT_CODE LIKE CONCAT('%',#{searchKeyword},'%')
and a.AGENT_CODE = #{agentCode}
and a.MSG_SEQ = #{msgSeq}
and a.XML_PROCESS_NAME = #{xmlProcessName}
and DATE_FORMAT(now(),'%Y%m%d') between #{schStartDay} and #{schEndDay}
ORDER BY a.MSG_SEQ DESC ) TB ORDER BY TB.msgSeq DESC limit #{firstIndex}, #{recordCountPerPage}


        SELECT IFNULL(COUNT(*),0) 
        FROM  TB_AGENTSENDMESSAGEINFO a, TB_AGENTINFO b, TB_SENDMESSAGETYPR c
	    WHERE a.AGENT_CODE =  b.AGENT_CODE and        a.XML_PROCESS_NAME = c.XML_PROCESS_NAME
                          AND e.AGENT_CODE = a.AGENT_CODE AND d.GROUP_CODE = e.GROUP_CODE	        



								and C.XML_PROCESS_NAME LIKE CONCAT('%',#{searchKeyword},'%') 


								and A.AGENT_CODE LIKE  CONCAT('%',#{searchKeyword},'%') 




				   and A.AGENT_CODE = #{agentCode} 


				   and A.MSG_SEQ = #{msgSeq} 


				  and A.XML_PROCESS_NAME = #{xmlProcessName}


				  and DATE_FORMAT(now(),'%Y%m%d') between #{schStartDay} and #{schEndDay}

mapper.update:
UPDATE TB_AGENTSENDMESSAGEINFO set SEND_RESULT = #{sendResult}
                                   , AGENT_MAC = #{agentMac}
                                   , AGENT_PLAYTIME = now()
                                   , ERROR_MESSAGE = #{errorMessage}
WHERE      MSG_SEQ = #{msgSeq}
