mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.AGENT_CODE, a.AGENT_NM, a.AGENT_REMARK, a.AGENT_IP, a.AGENT_MAC, a.PART_ID, a.CENTER_ID, a.AGENT_USEYN, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN a.DISPLAY_SEQ ELSE (SELECT d.DISPLAY_SEQ FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND DATE_FORMAT(NOW(), '%Y%m%d') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displaySeq, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN (select DISPLAY_TITLE from TB_DISPLAYPAGEINFO b WHERE a.DISPLAY_SEQ = b.DISPLAY_SEQ ) ELSE (SELECT d.DISPLAY_TITLE FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND DATE_FORMAT(NOW(), '%Y%m%d') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displayTitle, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN (SELECT DISPALY_TOTALTIME FROM TB_DISPLAYPAGEINFO b WHERE a.DISPLAY_SEQ = b.DISPLAY_SEQ ) ELSE (SELECT d.DISPALY_TOTALTIME FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND DATE_FORMAT(NOW(), '%Y%m%d') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displayTotalTime,
a.AGENT_FLOOR, fn_DetailCodeNm(a.AGENT_GUBUN) as agentGubun, CONCAT(a.agent_starttime,'~',agent_endtime) as agentStarttime, fn_DetailCodeNm(a.AGENT_CONTENTGUBUN) as contentGubun, a.AGENT_STATE, a.CONN_DATE, c.CENTER_NM FROM TB_AGENTINFO a, TB_CENTERINFO c WHERE a.CENTER_ID = c.CENTER_ID
AND a.AGENT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND a.DISPLAY_SEQ = #{searchKeyword}
AND a.AGENT_REMARK LIKE CONCAT('%',#{searchKeyword},'%')
AND a.DISPLAYSEQ = #{searchDisplayseq}
AND a.CENTER_ID = #{searchCenterid}
AND a.AGENT_FLOOR = #{searchFloor}
AND a.AGENT_STATE =#{searchAgentState}
AND a.PART_ID = #{searchPartid}
AND a.PART_ID IN ( SELECT PART_ID FROM TB_PARTINFO WHERE PART_ID = #{partId} UNION ALL SELECT hi.part_id FROM ( SELECT fn_hierarchy(part_id) AS partId, @level AS level FROM ( SELECT @start_with := #{partId}, @partId := @start_with, @level := 0 ) vars, TB_PARTINFO WHERE @partId IS NOT NULL ) ho JOIN TB_PARTINFO hi ON hi.PART_ID = ho.PARTID )
ORDER BY a.AGENT_CODE DESC )TB ORDER BY TB.AGENT_CODE DESC limit #{firstIndex}, #{recordCountPerPage}

      SELECT X1.agentCnt , X1.agentState, X1.CENTER_NM, X1.tCnt, ROUND( ((X1.agentCnt/X1.TCnt)*100) ,2) statePer FROM (
			SELECT IFNULL(count(X.AGENT_STATE),0) agentCnt, IFNULL(X.AGENT_STATE, 'F') agentState, X.CENTER_NM ,  X.CENTER_ID, X.TCnt FROM (
				SELECT  AGENT_STATE , b.CENTER_NM, a.CENTER_ID, SUM(1) OVER(PARTITION BY NULL) AS TCnt
				FROM TB_AGENTINFO a , TB_CENTERINFO b 
				WHERE a.CENTER_ID = b.CENTER_ID
				      AND a.AGENT_USEYN = 'Y'
				      AND a.CENTER_ID = #{centerId} 
			) X 
			GROUP BY X.AGENT_STATE
		) X1

SELECT ifnull(count(*),0)   
FROM TB_AGENTINFO
WHERE AGENT_CODE = #{agentCode}

SELECT AGENT_CODE, AGENT_NM,AGENT_MAC, UPDATE_CHANGE,UPDATE_DATE,AGENT_CONTENTGUBUN
FROM TB_AGENTINFO
WHERE  AGENT_CODE = #{agentCode} AND AGENT_MAC = #{agentMac}

mapper.update:
UPDATE TB_AGENTINFO SET AGENT_IP = #{agentIp,jdbcType=VARCHAR},
                        AGENT_MAC = #{agentMac,jdbcType=VARCHAR }
WHERE   AGENT_CODE = #{agentCode}

UPDATE TB_AGENTINFO SET UPDATE_CHANGE = #{updateChange}, 
						 UPDATE_DATE = now()
WHERE AGENT_CODE = #{agentCode}

UPDATE TB_AGENTINFO set AGENT_NM = #{agentNm}
                       ,  AGENT_REMARK = #{agentRemark,jdbcType=VARCHAR }
                       ,  DISPLAY_SEQ = #{displaySeq,jdbcType=INTEGER  }
                       ,  AGENT_USEYN =  #{agentUseYn,jdbcType=VARCHAR }
                       ,  LAST_UPDT_PNTTM = now()
                       ,  LAST_UPDUSR_ID = #{userId,jdbcType=VARCHAR}
                       ,  CENTER_ID = #{centerId ,jdbcType=VARCHAR}
                       ,  PART_ID = #{partId, jdbcType=VARCHAR}
                       ,  agent_starttime = #{agentStarttime, jdbcType=VARCHAR}
                       ,  agent_endtime = #{agentEndtime, jdbcType=VARCHAR}
                       ,  agent_floor = #{agentFloor, jdbcType=INTEGER }
                       ,  AGENT_GUBUN = #{agentGubun, jdbcType=VARCHAR }
                       ,  AGENT_CONTENTGUBUN = #{agentContentgubun, jdbcType=VARCHAR }
WHERE AGENT_CODE = #{agentCode}
