mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT CL_CODE, CL_CODE_NM, USE_AT, DATE_FORMAT(LAST_UPDT_PNTTM, '%Y-%m-%d %H:%I') AS LAST_UPDT_PNTTM, LAST_UPDUSR_ID, CL_CODE_DC FROM COMTCCMMNCLCODE a WHERE 1=1
AND a.CL_CODE LIKE CONCAT('%',#{params.searchKeyword},'%')
and a.CL_CODE_NM LIKE CONCAT('%',#{params.searchKeyword},'%')
AND a.USE_YN = #{params.useYn}
ORDER BY a.LAST_UPDT_PNTTM DESC ) TB LIMIT #{params.firstIndex} , #{params.recordCountPerPage}

SELECT CL_CODE, CL_CODE_NM, USE_AT, CL_CODE_DC, FRST_REGISTER_ID
FROM COMTCCMMNCLCODE
WHERE CL_CODE = #{value}

mapper.insert:

INSERT  INTO  COMTCCMMNCLCODE
	(CL_CODE  ,  CL_CODE_NM ,  CL_CODE_DC ,  USE_AT ,  
	FRST_REGIST_PNTTM ,  FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID) 
VALUES ( #{clCode} ,  #{clCodeNm} ,  #{clCodeDc} ,  #{useAt} ,  
		NOW() ,  #{userId,jdbcType=VARCHAR}, NOW() ,  #{userId,jdbcType=VARCHAR}
		)

mapper.delete:
DELETE FROM COMTCCMMNCLCODE 
WHERE CL_CODE = #{value};

DELETE FROM COMTCCMMNCODE 
WHERE CL_CODE = #{value};

DELETE FROM COMTCCMMNDETAILCODE 
WHERE CODE_ID IN (SELECT CODE_ID FROM COMTCCMMNCODE WHERE CL_CODE = #{value});
