mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM (SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, ROLE_ID, ROLE_NAME, ROLE_DC, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d') AS ROLE_CREAT_DE, ROLE_USEYN, (SELECT IFNULL(COUNT(B.ROLE_ID), 0) FROM COMTNMENUCREATDTLS B WHERE B.ROLE_ID = A.ROLE_ID AND B.SYSTEM_CODE = #{params.systemCode} ) AS "chk_menu" FROM TB_ROLEINFO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND ( ROLE_ID like CONCAT ('%', #{params.searchKeyword},'%') OR ROLE_NAME like CONCAT ('%', #{params.searchKeyword},'%') )
) TB LIMIT #{params.firstIndex},#{params.recordCountPerPage}


SELECT COUNT(ROLE_ID) AS "totcnt"
FROM COMTNMENUCREATDTLS
WHERE ROLE_ID = #{roleId}

SELECT b.ADMIN_ID , (SELECT DOMAIN_INFO FROM TB_SYSTEMINFO WHERE SYSTEM_CODE  = #{params.systemCode}) URL
FROM TB_ROLEINFO a, TB_MANAGERINFO b 
WHERE a.ROLE_ID  = b.ROLE_ID 
	  AND a.ROLE_ID =  #{params.roleId}

	  AND b.ADMIN_ID  IN ( SELECT USER_ID 
							FROM TB_MANAGER_AUTH_INFO
							WHERE  ROLE_ID  = #{params.roleId}
	  )
