mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.ADMIN_ID , a.ADMIN_NAME , b.MANAGE_SEQ, b.USER_ID, b.INSTT_CODE, b.PART_ID, b.ADMIN_STATUS, b.ADMIN_POSITION, b.LAST_UPDUSR_ID, b.STATE_REGDATE, b.CHANGE_DC FROM TB_MANAGERINFO a, TB_MANAGERINFO_STATE_CHANAGEINFO b, TB_PARTINFO c, COMTNINSTTCODE d WHERE 1=1 AND a.ADMIN_ID = b.USER_ID AND b.PART_ID = c.PART_ID AND b.INSTT_CODE = d.INSTT_CODE and a.ADMIN_ID = #{params.adminId}
and b.ADMIN_STATUS LIKE CONCAT('%',#{params.searchKeyword},'%')
and b.ADMIN_POSITION LIKE CONCAT('%',#{params.searchKeyword},'%')
and b.CHANGE_DC LIKE CONCAT('%',#{params.searchKeyword},'%')
AND a.USE_YN = #{params.useYn}
AND b.PART_ID = #{params.searchPartId}
ORDER BY a.REG_DATE DESC ) TB limit #{params.firstIndex} , #{params.recordCountPerPage}

mapper.insert:
UPDATE TB_MANAGERINFO SET LAST_UPDT_PNTTM = NOW()
						, LAST_UPDUSR_ID = #{updateId}

						, ADMIN_STATUS = #{adminStatus}


						, ADMIN_POSITION = #{adminPosition}

WHERE ADMIN_ID = #{userId} ;

INSERT INTO TB_MANAGERINFO_STATE_CHANAGEINFO(USER_ID, INSTT_CODE, PART_ID, ADMIN_STATUS, ADMIN_POSITION, 
											LAST_UPDUSR_ID, CHANGE_DC)
SELECT ADMIN_ID, INSTT_CODE, PART_ID, ADMIN_STATUS, ADMIN_POSITION, #{updateId},  #{changeDc, jdbcType=VARCHAR}
FROM TB_MANAGERINFO
WHERE ADMIN_ID = #{userId}
