mapper.select:
SELECT CONCAT(CONCAT(LPAD('', (3*(LEVEL -1)), ' '), IF (LEVEL >1, '└','')), HI.PART_NM) AS PART_NM_HI, HI.PART_NM, PARENT_PART_ID, LEVEL , HI.PART_ORDER, HI.PART_ID, HI.PART_USEYN, HI.PART_DC, HI.PART_CREATE_DE, HI.PART_HEAD_USER_ID, HI.PART_END_DE, HI.PART_ENDYN FROM ( SELECT FN_HIERARCHY(PART_ID) AS PARTID, @LEVEL AS LEVEL, @ROWNUM :=@ROWNUM + 1 AS ORDERS FROM ( SELECT @START_WITH := 0, @PARTID := @START_WITH, @LEVEL := 0 ) VARS, TB_PARTINFO , (SELECT @ROWNUM :=0) TMP WHERE @PARTID IS NOT NULL ) HO , TB_PARTINFO HI WHERE HO.PARTID =HI.PART_ID AND HI.PART_USEYN ='Y'
AND INSTT_CODE = #{params.searchInsttCode}
ORDER BY HO.ORDERS ASC

mapper.update:
UPDATE  TB_PARTINFO SET PART_NM = #{partNm} 
                        ,PART_DC = #{partDc,jdbcType=VARCHAR} 
                        ,part_USEYN = #{partUseyn,jdbcType=VARCHAR} 
                        ,PARENT_PART_ID = #{parentPartId}  
                        ,PART_ORDER =  #{partOrder, jdbcType=VARCHAR}
                        ,INSTT_CODE =  #{insttCode, jdbcType=VARCHAR}
                        ,PART_HEAD_USER_ID = #{partHeadUserId, jdbcType=VARCHAR}
                        ,PART_ENDYN = #{partEndyn, jdbcType=VARCHAR}

                        , PART_END_DE =  date_format(now(), '%Y%m%d%H%i%s')

WHERE PART_ID = #{partId};

UPDAETE TB_PARTINFO SET PART_ENDYN =  #{partEndyn, jdbcType=VARCHAR}
						, PART_END_DE =  date_format(now(), '%Y%m%d%H%i%s')
WHERE PART_ID = #{partId}
