mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROWNUM RNUM, X.* FROM ( SELECT a.AGENT_CODE, a.AGENT_NM, a.AGENT_REMARK, a.AGENT_IP, a.AGENT_MAC, a.AGENT_USEYN, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN a.DISPLAY_SEQ ELSE (SELECT d.DISPLAY_SEQ FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND TO_CHAR(SYSDATE, 'yyyyMMdd') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displaySeq, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN (SELECT DISPLAY_TITLE FROM TB_DISPLAYPAGEINFO b where a.DISPLAY_SEQ = b.DISPLAY_SEQ ) ELSE (SELECT d.DISPLAY_TITLE FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND TO_CHAR(SYSDATE, 'yyyyMMdd') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displayTitle, CASE a.AGENT_CONTENTGUBUN WHEN 'AGENT_CONTENT_1' THEN (select DISPALY_TOTALTIME FROM TB_DISPLAYPAGEINFO b WHERE a.DISPLAY_SEQ = b.DISPLAY_SEQ ) ELSE (SELECT d.DISPALY_TOTALTIME FROM TB_CONTENTSCHEDULE b, TB_AGENTGROUPINFO c , TB_DISPLAYPAGEINFO d WHERE b.CONSCH_CODE = c.CONSCH_CODE AND d.DISPLAY_SEQ = b.DISPLAY_SEQ AND c.AGENT_CODE = a.AGENT_CODE AND TO_CHAR(SYSDATE, 'yyyyMMdd') BETWEEN b.CONSCH_STARTDAY AND b.CONSCH_ENDDAY ) END AS displayTotalTime, FN_AgentState_nowState(a.AGENT_CODE) agentStateInfo, a.AGENT_FLOOR, fn_DetailCodeNm(a.AGENT_GUBUN) as agentGubun, (a.agent_starttime||'~'||agent_endtime) as agentStarttime, fn_DetailCodeNm(a.AGENT_CONTENTGUBUN) as contentGubun, a.AGENT_STATE, a.CONN_DATE, c.CENTER_NM FROM TB_AGENTINFO a, TB_CENTERINFO c WHERE a.CENTER_ID = c.CENTER_ID
AND a.AGENT_NM LIKE #{searchKeyword} || '%'
AND a.DISPLAY_SEQ = #{searchKeyword}
AND a.AGENT_REMARK LIKE #{searchKeyword} || '%'
AND a.displaySeq = #{searchDisplayseq}
AND a.CENTER_ID = #{searchCenterid}
AND a.AGENT_FLOOR = #{searchFloor}
AND a.AGENT_STATE =#{searchAgentState}
AND a.PART_ID = #{searchPartid}
AND a.PART_ID IN ( SELECT PART_ID FROM TB_PARTINFO where PART_ID = #{partId} UNION ALL SELECT PART_ID FROM TB_PARTINFO START WITH PARENT_PART_ID = #{partId} CONNECT BY PRIOR PART_ID = PARENT_PART_ID )
ORDER BY a.AGENT_CODE DESC ) X )TB WHERE TB.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage} ORDER BY TB.AGENT_CODE DESC

SELECT NVL(count(*),0)   
FROM TB_AGENTINFO
WHERE AGENT_CODE = #{agentCode}

SELECT AGENT_CODE, AGENT_NM,AGENT_MAC, UPDATE_CHANGE,UPDATE_DATE,AGENT_CONTENTGUBUN
FROM TB_AGENTINFO
WHERE  AGENT_CODE = #{agentCode} AND AGENT_MAC = #{agentMac}

mapper.update:
UPDATE tb_agentinfo SET AGENT_IP = #{agentIp,jdbcType=VARCHAR},
                                    AGENT_MAC = #{agentMac,jdbcType=VARCHAR }
WHERE   AGENT_CODE = #{agentCode}

UPDATE tb_agentinfo set AGENT_NM = #{agentNm}
                       ,  AGENT_REMARK = #{agentRemark,jdbcType=VARCHAR }
                       ,  DISPLAY_SEQ = #{displaySeq,jdbcType=INTEGER  }
                       ,  AGENT_USEYN =  #{agentUseYn,jdbcType=VARCHAR }
                       ,  LAST_UPDT_PNTTM = SYSDATE
                       ,  LAST_UPDUSR_ID = #{userId,jdbcType=VARCHAR}
                       , CENTER_ID = #{centerId ,jdbcType=VARCHAR}
                       , PART_ID = #{partId, jdbcType=VARCHAR}
                       , agent_starttime = #{agentStarttime, jdbcType=VARCHAR}
                       , agent_endtime = #{agentEndtime, jdbcType=VARCHAR}
                       , agent_floor = #{agentFloor, jdbcType=INTEGER }
                       , AGENT_GUBUN = #{agentGubun, jdbcType=VARCHAR }
                       , AGENT_CONTENTGUBUN = #{agentContentgubun, jdbcType=VARCHAR }
WHERE AGENT_CODE = #{agentCode}
