mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROWNUM RNUM, X.* FROM ( SELECT a.REQUST_ID, a.OCCRRNC_DE OCCRRNC_DE, a.SRVC_NM, a.METHOD_NM, a.PROCESS_SE_CODE, a.PROCESS_TIME, a.ERROR_SE, a.ERROR_CODE, a.ERROR_CO, a.RSPNS_CODE, a.RQESTER_IP, a.RQESTER_ID, a.SQL_PARAM, a.SQL_QUERY, a.METHOD_RESULT, b.ADMIN_NAME, c.PART_ID, c.PART_NM, CASE a.PROCESS_SE_CODE WHEN 'I' then '입력' WHEN 'U' then '수정' WHEN 'D' then '삭제' WHEN 'S' then '조회' ELSE '에러' END PROCESS_SE_CODE_TXT FROM COMTNSYSLOG a, TB_MANAGERINFO b, TB_PARTINFO c WHERE a.RQESTER_ID = b.ADMIN_ID AND b.PART_ID = c.PART_ID
AND a.OCCRRNC_DE >= #{searchStartdday}
AND a.OCCRRNC_DE <= #{searchEndday}
AND b.ADMIN_NAME LIKE '%' || #{searchKeyword} || '%'
AND a.CONECT_ID LIKE '%' || #{searchKeyword} || '%'
AND a.PROCESS_SE_CODE = #{searchProcessGubun}
ORDER BY a.REQUST_ID DESC )X )TB WHERE TB.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage} ORDER BY TB.REQUST_ID DESC

mapper.insert:

		INSERT INTO COMTNSYSLOG
			( REQUST_ID
			  , SRVC_NM
			  , METHOD_NM
			  , PROCESS_SE_CODE
			  , PROCESS_TIME
			  , RQESTER_ID
			  , RQESTER_IP
			  , ERROR_CODE
			  , OCCRRNC_DE 
			  , SQLID
			  , SQL_PARAM
			  , SQL_QUERY
			  , METHOD_RESULT
			  )
		VALUES ( #{requstId}
			  , #{srvcNm}
			  , #{methodNm}
			  , #{processSeCode}
			  , #{processTime}
			  , #{rqesterId}
			  , #{rqesterIp}
			  , #{errorCode}
			  , TO_CHAR(sysdate,'yyyyMMdd')
			  , #{sqlid ,jdbcType=VARCHAR}
			  , #{sqlParam, jdbcType=VARCHAR}
			  , #{sqlQuery, jdbcType=VARCHAR}
			   , #{methodResult, jdbcType=VARCHAR}
        	  )
